import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
import { toast } from "react-toastify";
import Layout from "../components/layout/Layout";
import { getCart } from "../features/cart/cartSlice";
import {
  createOrder,
  createPaymentIntent,
  confirmPayment,
} from "../features/orders/orderSlice";
import { clearCart } from "../features/cart/cartSlice";
import CheckoutForm from "../components/checkout/CheckoutForm";
import OrderSummary from "../components/checkout/OrderSummary";
import PaymentForm from "../components/checkout/PaymentForm";
import OrderConfirmation from "../components/checkout/OrderConfirmation";

// Load Stripe (use test publishable key)
const stripePromise = loadStripe(
  process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY ||
    "pk_test_your_stripe_publishable_key_here"
);

const Checkout = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const { user } = useSelector((state) => state.auth);
  const { items, subtotal, shippingCost, discount, total, isLoading } =
    useSelector((state) => state.cart);
  const { order, isLoading: orderLoading } = useSelector(
    (state) => state.orders
  );

  const [step, setStep] = useState(1);
  const [shippingData, setShippingData] = useState({
    firstName: user?.name?.split(" ")[0] || "",
    lastName: user?.name?.split(" ").slice(1).join(" ") || "",
    email: user?.email || "",
    phone: "",
    address: "",
    city: "",
    state: "",
    zipCode: "",
    country: "US",
    notes: "",
  });
  const [paymentMethod, setPaymentMethod] = useState("card");
  const [clientSecret, setClientSecret] = useState("");
  const [orderId, setOrderId] = useState(null);

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }

    if (items.length === 0) {
      navigate("/cart");
      return;
    }

    dispatch(getCart());
  }, [dispatch, user, navigate, items.length]);

  const handleShippingSubmit = (data) => {
    setShippingData(data);
    setStep(2);
  };

  const handlePaymentMethodSelect = (method) => {
    setPaymentMethod(method);
  };

  const handleCreateOrder = async () => {
    try {
      // Map frontend payment method to backend expected value
      const backendPaymentMethod =
        paymentMethod === "card" ? "stripe" : paymentMethod;
      const orderData = {
        shippingAddress: shippingData,
        paymentMethod: backendPaymentMethod,
        notes: shippingData.notes,
      };

      const result = await dispatch(createOrder(orderData)).unwrap();
      setOrderId(result.id);

      // Create payment intent
      const paymentResult = await dispatch(
        createPaymentIntent(result.id)
      ).unwrap();
      setClientSecret(paymentResult.clientSecret);

      setStep(3);
    } catch (error) {
      toast.error(error || "Failed to create order");
    }
  };

  const handlePaymentSuccess = async (paymentIntentId) => {
    try {
      await dispatch(confirmPayment({ orderId, paymentIntentId })).unwrap();
      await dispatch(clearCart());
      setStep(4);
      toast.success("Order placed successfully!");
    } catch (error) {
      toast.error(error || "Payment confirmation failed");
    }
  };

  const handleBackToShop = () => {
    navigate("/products");
  };

  if (isLoading || orderLoading) {
    return (
      <Layout>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
          </div>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Progress Steps */}
        <div className="mb-8">
          <div className="flex items-center justify-center space-x-4">
            {[1, 2, 3, 4].map((stepNumber) => (
              <div key={stepNumber} className="flex items-center">
                <div
                  className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
                    step >= stepNumber
                      ? "bg-blue-600 text-white"
                      : "bg-gray-200 text-gray-600"
                  }`}>
                  {stepNumber}
                </div>
                {stepNumber < 4 && (
                  <div
                    className={`w-16 h-0.5 mx-2 ${
                      step > stepNumber ? "bg-blue-600" : "bg-gray-200"
                    }`}></div>
                )}
              </div>
            ))}
          </div>
          <div className="flex justify-center mt-2 space-x-16">
            <span
              className={`text-sm ${
                step >= 1 ? "text-blue-600" : "text-gray-500"
              }`}>
              Shipping
            </span>
            <span
              className={`text-sm ${
                step >= 2 ? "text-blue-600" : "text-gray-500"
              }`}>
              Payment
            </span>
            <span
              className={`text-sm ${
                step >= 3 ? "text-blue-600" : "text-gray-500"
              }`}>
              Review
            </span>
            <span
              className={`text-sm ${
                step >= 4 ? "text-blue-600" : "text-gray-500"
              }`}>
              Complete
            </span>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-2">
            {step === 1 && (
              <CheckoutForm
                initialData={shippingData}
                onSubmit={handleShippingSubmit}
              />
            )}

            {step === 2 && (
              <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
                <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">
                  Payment Method
                </h2>
                <div className="space-y-4">
                  <div className="flex items-center space-x-3">
                    <input
                      type="radio"
                      id="card"
                      name="paymentMethod"
                      value="card"
                      checked={paymentMethod === "card"}
                      onChange={() => handlePaymentMethodSelect("card")}
                      className="w-4 h-4 text-blue-600"
                    />
                    <label
                      htmlFor="card"
                      className="text-gray-700 dark:text-gray-300">
                      Credit/Debit Card
                    </label>
                  </div>
                  <div className="flex items-center space-x-3">
                    <input
                      type="radio"
                      id="paypal"
                      name="paymentMethod"
                      value="paypal"
                      checked={paymentMethod === "paypal"}
                      onChange={() => handlePaymentMethodSelect("paypal")}
                      className="w-4 h-4 text-blue-600"
                    />
                    <label
                      htmlFor="paypal"
                      className="text-gray-700 dark:text-gray-300">
                      PayPal
                    </label>
                  </div>
                </div>
                <div className="mt-6 flex space-x-4">
                  <button
                    onClick={() => setStep(1)}
                    className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50">
                    Back
                  </button>
                  <button
                    onClick={handleCreateOrder}
                    className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700">
                    Continue to Payment
                  </button>
                </div>
              </div>
            )}

            {step === 3 && clientSecret && (
              <Elements stripe={stripePromise} options={{ clientSecret }}>
                <PaymentForm
                  onSuccess={handlePaymentSuccess}
                  onBack={() => setStep(2)}
                  clientSecret={clientSecret}
                />
              </Elements>
            )}

            {step === 4 && order && (
              <OrderConfirmation
                order={order}
                onBackToShop={handleBackToShop}
              />
            )}
          </div>

          {/* Order Summary */}
          <div className="lg:col-span-1">
            <OrderSummary
              items={items}
              subtotal={subtotal}
              shippingCost={shippingCost}
              discount={discount}
              total={total}
              step={step}
            />
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default Checkout;
